{"filePath":"f:\codestorm\codestorm\stormcom-ui\stormcom\src\repos\base.ts","content":"import prisma from "@/lib/prisma";
import { Prisma } from "@prisma/client";

export interface RepoContext {
  storeId: string;
}

export abstract class BaseRepo {
  protected ctx: RepoContext;
  constructor(ctx: RepoContext) {
    this.ctx = ctx;
  }
}

export class ProductRepo extends BaseRepo {
  /**
   * List products using ProductSummary when available for performance.
   */
  async list(options: { take?: number; skip?: number; featured?: boolean } = {}) {
    const { take = 50, skip = 0, featured } = options;
    // Try ProductSummary first; fallback to Product
    const summaries = await prisma.productSummary.findMany({
      where: {
        storeId: this.ctx.storeId,
        ...(featured !== undefined ? { featured } : {}),
      },
      take,
      skip,
      orderBy: { updatedAt: "desc" },
    }).catch(() => []);

    if (summaries.length) return summaries;

    return prisma.product.findMany({
      where: { storeId: this.ctx.storeId },
      take,
      skip,
      orderBy: { updatedAt: "desc" },
      select: {
        id: true,
        name: true,
        slug: true,
        price: true,
        inventoryStatus: true,
        updatedAt: true,
        isFeatured: true,
      },
    });
  }
}

export function createScopedRepos(storeId: string) {
  return {
    products: new ProductRepo({ storeId }),
  };
}
"}

