export class StockReservationRepo {
  private storeId: string;
  constructor(storeId: string) {
    this.storeId = storeId;
  }

  /**
   * Reserve stock for a product (and optional variant) with TTL.
   * Ensures available inventory considers existing active (non-expired, non-released) reservations.
   */
  async reserve(params: { productId: string; variantId?: string; quantity: number; ttlSeconds?: number }) {
    const { productId, variantId, quantity, ttlSeconds = 900 } = params; // default 15m
    if (quantity <= 0) throw new Error("Quantity must be positive");
    const now = new Date();
    const expiresAt = new Date(now.getTime() + ttlSeconds * 1000);

    return prisma.$transaction(async (tx) => {
      const product = await tx.product.findFirst({
        where: { id: productId, storeId: this.storeId },
        select: { id: true, inventoryQty: true, trackInventory: true },
      });
      if (!product) throw new Error("Product not found in store scope");
      if (!product.trackInventory) {
        // Non-tracked inventory: just create reservation (optional behavior)
        return tx.stockReservation.create({
          data: { storeId: this.storeId, productId, variantId, quantity, expiresAt },
        });
      }

      // Sum active reservations
      const activeAgg = await tx.stockReservation.aggregate({
        _sum: { quantity: true },
        where: {
          storeId: this.storeId,
          productId,
          variantId: variantId ?? undefined,
          releasedAt: null,
          expiresAt: { gt: now },
        },
      });
      const activeReserved = activeAgg._sum.quantity ?? 0;
      const available = product.inventoryQty - activeReserved;
      if (available < quantity) {
        throw new Error(`Insufficient available inventory. Requested ${quantity}, available ${available}.`);
      }
      return tx.stockReservation.create({
        data: { storeId: this.storeId, productId, variantId, quantity, expiresAt },
      });
    });
  }

  /** Release a reservation explicitly (e.g., user abandons checkout) */
  async release(reservationId: string) {
    return prisma.stockReservation.update({
      where: { id: reservationId },
      data: { releasedAt: new Date() },
    });
  }

  /** Attach reservations to an order and mark them released (inventory should already have been decremented in order flow). */
  async commitToOrder(orderId: string, reservationIds: string[]) {
    if (!reservationIds.length) return [];
    const now = new Date();
    return prisma.$transaction(async (tx) => {
      const updates = [] as any[];
      for (const id of reservationIds) {
        const res = await tx.stockReservation.update({
          where: { id },
          data: { orderId, releasedAt: now },
        });
        updates.push(res);
      }
      return updates;
    });
  }

  /** Cleanup expired reservations (mark releasedAt) */
  async releaseExpired(batchSize = 200) {
    const now = new Date();
    const expired = await prisma.stockReservation.findMany({
      where: { storeId: this.storeId, expiresAt: { lt: now }, releasedAt: null },
      take: batchSize,
      orderBy: { expiresAt: "asc" },
      select: { id: true },
    });
    if (!expired.length) return 0;
    await prisma.stockReservation.updateMany({
      where: { id: { in: expired.map((e) => e.id) } },
      data: { releasedAt: now },
    });
    return expired.length;
  }
}

export function createInventoryRepo(storeId: string) {
  return new StockReservationRepo(storeId);
}
"}

