/*
 Backfill script for ProductSummary denormalized table.
 Populates or updates ProductSummary rows from existing Product + Category + Brand data.
 Usage (after compiling or with ts-node):
   npx ts-node scripts/backfill-product-summary.ts
 Ensure DATABASE_URL is set (source .env.local on Windows Powershell: setx DATABASE_URL "file:./dev.db" or use dotenv-cli).
*/
import prisma from "@/lib/prisma";

async function run() {
  const products = await prisma.product.findMany({
    select: {
      id: true,
      storeId: true,
      name: true,
      slug: true,
      price: true,
      inventoryStatus: true,
      isFeatured: true,
      category: { select: { slug: true } },
      brand: { select: { slug: true } },
      updatedAt: true,
    },
  });
  let created = 0;
  let updated = 0;
  for (const p of products) {
    await prisma.productSummary.upsert({
      where: { productId: p.id },
      update: {
        name: p.name,
        slug: p.slug,
        price: p.price,
        inventoryStatus: p.inventoryStatus,
        featured: p.isFeatured,
        categorySlug: p.category?.slug,
        brandSlug: p.brand?.slug,
        cachedAt: new Date(),
      },
      create: {
        productId: p.id,
        storeId: p.storeId,
        name: p.name,
        slug: p.slug,
        price: p.price,
        inventoryStatus: p.inventoryStatus,
        featured: p.isFeatured,
        categorySlug: p.category?.slug,
        brandSlug: p.brand?.slug,
      },
    }).then((res) => {
      // crude heuristic: if cachedAt is set on update path we treat as updated
      if (res.cachedAt.getTime() === res.updatedAt.getTime()) created++; else updated++;
    });
  }
  console.log(`ProductSummary backfill complete. processed=${products.length} created=${created} updated=${updated}`);
}

run().then(() => prisma.$disconnect()).catch((err) => { console.error(err); prisma.$disconnect(); process.exit(1); });
"}

